                .isNotEmpty()
                .hasMaxLength(15)
            .ensure(&quot;orderDate&quot;)
                .isNotEmpty()
            .ensure(&quot;requiredDate&quot;)
                .isNotEmpty()
            .ensure(&quot;shippedDate&quot;)
                .isNotEmpty()
            .ensure(&quot;shipName&quot;)
                 .isNotEmpty()
                .hasMaxLength(40)
            .ensure(&quot;shipAddress&quot;)
                .isNotEmpty()
                .hasMaxLength(60)
            .ensure(&quot;shipCity&quot;)
                .isNotEmpty()
                .hasMaxLength(15)
            .ensure(&quot;shipPostalCode&quot;)
                .isNotEmpty()
                .hasMaxLength(15)
            .ensure(&quot;shipCountry&quot;)
                .isNotEmpty()
