   &lt;template&gt;
      &lt;section&gt;
        &lt;div class=&quot;panel panel-default&quot;&gt;
            &lt;div class=&quot;panel-body&quot;&gt;
                &lt;div class=&quot;row&quot;&gt;
                    &lt;div class=&quot;col-md-6&quot;&gt;
                        &lt;br /&gt;
                        &lt;div class=&quot;input-group&quot;&gt;
                            &lt;input type=&quot;text&quot; value.bind=&quot;datasource.searchCriteria&quot; keyup.delegate=&quot;onKeyUp($event)&quot;
                                   class=&quot;form-control&quot; placeholder=&quot;category Name or description&quot;&gt;
                            &lt;div class=&quot;input-group-btn&quot;&gt;
                                &lt;button type=&quot;button&quot; class=&quot;btn btn-success&quot; tabindex=&quot;-1&quot;
                                        click.trigger=&quot;searchAll()&quot;&gt;
                                    Search
                                &lt;/button&gt;
                                &lt;button type=&quot;button&quot; class=&quot;btn btn-success dropdown-toggle&quot; data-toggle=&quot;dropdown&quot; tabindex=&quot;-1&quot;&gt;
                                    &lt;span class=&quot;caret&quot;&gt;&lt;/span&gt;
                                    &lt;span class=&quot;sr-only&quot;&gt;Toggle Dropdown&lt;/span&gt;
                                &lt;/button&gt;
                                &lt;ul class=&quot;dropdown-menu pull-right&quot;&gt;&lt;/ul&gt;
                            &lt;/div&gt;
                        &lt;/div&gt;
                    &lt;/div&gt;
                    &lt;div class=&quot;col-md-6&quot;&gt;
                    &lt;/div&gt;
                &lt;/div&gt;
                &lt;hr /&gt;
                &lt;table class=&quot;table table-condensed&quot;&gt;
                    &lt;thead&gt;
                        &lt;tr&gt;
                            &lt;td&gt;Category Name&lt;/td&gt;
                            &lt;td&gt;Description&lt;/td&gt;
                            &lt;td&gt;&lt;/td&gt;
                            &lt;td&gt;&lt;/td&gt;
                        &lt;/tr&gt;
                    &lt;/thead&gt;
                    &lt;tbody&gt;
                        &lt;tr repeat.for=&quot;cat of  datasource.displaySource&quot;&gt;
                            &lt;td&gt;${cat.categoryName}&lt;/td&gt;
                            &lt;td&gt;${cat.description}&lt;/td&gt;
                            &lt;td&gt;&lt;a route-href=&quot;route: categorydetails; params.bind: {id: cat.categoryID}&quot;&gt;&lt;i class=&quot;fa fa-file-text&quot;&gt;&lt;/i&gt; Details &lt;/a&gt;&lt;/td&gt;
                            &lt;td&gt;&lt;a route-href=&quot;route: categoryedit; params.bind: {id:cat.categoryID}&quot;&gt;&lt;i class=&quot;fa fa-pencil-square-o&quot;&gt;&lt;/i&gt; Edit&lt;/a&gt;&lt;/td&gt;
                            &lt;td&gt;&lt;a route-href=&quot;route: categoryDelete; params.bind: {id:cat.categoryID}&quot;&gt;&lt;i class=&quot;fa fa-trash-o&quot; style=&quot;color:red&quot;&gt;&lt;/i&gt; Delete&lt;/a&gt;&lt;/td&gt;
                        &lt;/tr&gt;
                    &lt;/tbody&gt;
                &lt;/table&gt;
                &lt;p&gt;&lt;/p&gt;
                &lt;hr /&gt;
                &lt;data-source source.bind=&quot;datasource&quot;&gt;&lt;/data-source&gt;
                &lt;hr /&gt;
                &lt;pager-source source.bind=&quot;datasource&quot;&gt;&lt;/pager-source&gt;
            &lt;/div&gt;
        &lt;/div&gt;
     &lt;/section&gt;
&lt;/template&gt;

import {inject} from &quot;aurelia-framework&quot;;
import {SourceManager} from &quot;../modules/SourceManager&quot;;
import {CategoriesDataContext} from &quot;./categoriesDataContext&quot;;
import _ from &#39;lodash&#39;;

@inject(SourceManager, CategoriesDataContext)
export class categories {

   constructor(datasource, datacontext) {
      this.title = &quot;List Categories&quot;;
       this.datasource = new SourceManager(5);
       this.datacontext = datacontext;

   }
    activate() {
       return this.datacontext.getAll()
           .then(result =&gt; {
               this.datasource.pageInit(result.categories)
           });
    }
    onKeyUp(ev) {
        if (ev.keyCode === 13) {
            this.searchAll();
        }
    }
    searchAll() {
       if (this.datasource.searchCriteria !== &#39;&#39;) {
          var filter = this.datasource.searchCriteria.trim().toLowerCase();
          var categoriesResults = _.filter(this.datasource.sourceCache,
               function (cat) {
                   return _.includes(cat.categoryName.trim().toLowerCase(),
                       filter.trim().toLowerCase()) ||
                       _.includes(cat.description.trim().toLowerCase(),
                           filter.trim().toLowerCase());

               });
           this.datasource.pageSearch(categoriesResults);
       } else {
           this.datasource.pageSearch(this.datasource.sourceCache);
       }
    }
}

import {inject} from &quot;aurelia-framework&quot;;
import {ContextBaseFetch} from &quot;../modules/contextBaseFetch&quot;;
import {AppConstants} from &quot;../modules/appConstants&quot;;
@inject(ContextBaseFetch)
export class CategoriesDataContext {
      url = AppConstants.urlBase +&quot;CategoriesApi&quot;; 

    constructor(context) {
        this.context = context;
        this.context.apiName = this.url;
    }
    getAll() {
        this.context.apiName = this.url;
         return this.context.getAll()
            .then(result =&gt; { return result });
    }
    getById(id) {
        this.context.apiName = this.url;
        return this.context.getById(id)
          
    }
    deleteRecord(id) {
        this.context.apiName = this.url;
        return this.context.deleteByID(id);
            
    }
    update(category) {
        this.context.apiName = this.url;
         return this.context.update(category, category.categoryID);
    }
    save(category) {
       this.context.apiName = this.url;
       return this.context.save(category);
    }
}

import {inject} from &quot;aurelia-framework&quot;;
import {HttpClient, json} from &#39;aurelia-fetch-client&#39;;
import &#39;fetch&#39;;

@inject(HttpClient)
export class ContextBaseFetch {
    apiName;
    constructor(http) {
        http.configure(config =&gt; {
            config
                .useStandardConfiguration()
                .withBaseUrl(this.apiName);
        });

        this.http = http;
    }
    
    getAll() {
        return this.http.fetch(this.apiName)
            .then(response =&gt; response.json())
            .then(result =&gt; {return result});
    }
    getById(id) {
        return this.http.fetch(this.apiName + &quot;/&quot; + id)
            .then(response =&gt; response.json())
            .then(result =&gt; { return result });
    }
    deleteByID(id) {
        return this.http.fetch(this.apiName + &quot;/&quot; + id,
            {
                headers: { &quot;content-type&quot;: &quot;application/json; charset=utf-8&quot;},
                method: &quot;DELETE&quot;
            })
            .then(response =&gt; response.json())
            .then(result =&gt; { return result });      
    }
     deleteData(id, prodid) {
        return this.http.fetch(this.apiName + &quot;/&quot;+ id +&quot;?prodid=&quot; + prodid,
            {
                headers: { &quot;content-type&quot;: &quot;application/json; charset=utf-8&quot;},
                method: &quot;DELETE&quot;
            })
            .then(response =&gt; response.json())
            .then(result =&gt; { return result });      
    }
    update(data,id) {
       return this.http.fetch(this.apiName + &quot;/&quot; + id, {
            headers: {&quot;content-type&quot;: &quot;application/json; charset=utf-8&quot;},
            method: &quot;PUT&quot;,
            body: json(data)
            }).then(response =&gt; response.json())
            .then(result =&gt; { return result });
    }
    save(data) {
       return this.http.fetch(this.apiName + &quot;/&quot; , {
            headers: { &quot;content-type&quot;: &quot;application/json; charset=utf-8&quot; },
            method: &quot;POST&quot;,
            body: json(data)
        }).then(response =&gt; response.json())
         .then(result =&gt; { return result });
   }

}
