&lt;template&gt;
    &lt;section&gt;
        &lt;div&gt;
            &lt;br /&gt;
            &lt;div class=&quot;col-md-4&quot;&gt;
                &lt;div class=&quot;panel panel-default&quot;&gt;
                    &lt;div class=&quot;panel-heading&quot;&gt;
                        &lt;div class=&quot;panel-title&quot;&gt;
                            &lt;h5&gt; List categories&lt;/h5&gt;
                        &lt;/div&gt;
                    &lt;/div&gt;
                    &lt;div class=&quot;panel-body&quot;&gt;
                        &lt;div class=&quot;input-group&quot;&gt;
                            &lt;input type=&quot;text&quot; value.bind=&quot;datasource.searchCriteria&quot; keyup.delegate=&quot;onKeyUp($event)&quot;
                                   class=&quot;form-control&quot; placeholder=&quot;Category Name, Description&quot;&gt;
                            &lt;div class=&quot;input-group-btn&quot;&gt;
                                &lt;button type=&quot;button&quot; class=&quot;btn btn-success&quot; tabindex=&quot;-1&quot;
                                        click.trigger=&quot;searchAll()&quot;&gt;
                                    Search
                                &lt;/button&gt;
                                &lt;button type=&quot;button&quot; class=&quot;btn btn-success dropdown-toggle&quot; data-toggle=&quot;dropdown&quot; tabindex=&quot;-1&quot;&gt;
                                    &lt;span class=&quot;caret&quot;&gt;&lt;/span&gt;
                                    &lt;span class=&quot;sr-only&quot;&gt;Toggle Dropdown&lt;/span&gt;
                                &lt;/button&gt;
                                &lt;ul class=&quot;dropdown-menu pull-right&quot;&gt;&lt;/ul&gt;
                            &lt;/div&gt;&lt;!-- /btn-group --&gt;

                        &lt;/div&gt;&lt;!-- /input-group --&gt;
                        &lt;br /&gt;
                        &lt;table class=&quot;table table-condensed table-responsive&quot;&gt;
                            &lt;thead&gt;
                                &lt;tr&gt;
                                    &lt;td&gt;category&lt;/td&gt;
                                &lt;/tr&gt;
                            &lt;/thead&gt;
                            &lt;tbody&gt;
                                &lt;tr repeat.for=&quot;cat of  datasource.displaySource&quot;&gt;
                                    &lt;td&gt;&lt;a href=&quot;#&quot; click.delegate=&quot;$parent.itemChange(cat)&quot;&gt;${cat.categoryName}&lt;/a&gt;&lt;/td&gt;
                                &lt;/tr&gt;
                            &lt;/tbody&gt;
                        &lt;/table&gt;
                        &lt;data-source source.bind=&quot;datasource&quot; click.delegate=&quot;itemChange(datasource.currentRecord)&quot;&gt;&lt;/data-source&gt;
                    &lt;/div&gt;
                &lt;/div&gt;
            &lt;/div&gt;
            &lt;div class=&quot;col-md-8&quot;&gt;
                &lt;div class=&quot;panel panel-default&quot;&gt;

                    &lt;div class=&quot;panel-body&quot;&gt;
                        &lt;section&gt;

                            &lt;ul class=&quot;nav nav-tabs&quot; id=&quot;categoriesAndProductTab&quot;&gt;
                                &lt;li&gt;&lt;a href=&quot;#categoriesAppTab&quot; data-toggle=&quot;tab&quot;&gt;Categories&lt;/a&gt;&lt;/li&gt;
                                &lt;li&gt;&lt;a href=&quot;#CategoryProductsAppTab&quot; data-toggle=&quot;tab&quot;&gt;Products&lt;/a&gt;&lt;/li&gt;
                            &lt;/ul&gt;
                            &lt;!-- Tab panes --&gt;
                            &lt;div class=&quot;tab-content&quot;&gt;
                                &lt;div class=&quot;tab-pane active&quot; id=&quot;categoriesAppTab&quot;&gt;
                                    &lt;div&gt;
                                        &lt;br /&gt;
                                        &lt;form class=&quot;form-horizontal&quot; role=&quot;form&quot;&gt;
                                            &lt;div&gt;
                                                &lt;div&gt;
                                                    &lt;div class=&quot;form-group&quot;&gt;
                                                        &lt;label for=&quot;CategoryID&quot; class=&quot;control-label col-md-3&quot;&gt;Category ID&lt;/label&gt;
                                                        &lt;div class=&quot;col-md-6&quot;&gt;
                                                            &lt;input type=&quot;text&quot; value.bind=&quot;datasource.currentRecord.categoryID&quot; id=&quot;CategoryID&quot; name=&quot;CategoryID&quot; class=&quot;form-control&quot; title=&quot;CategoryID&quot; placeholder=&quot;CategoryID&quot; /&gt;
                                                        &lt;/div&gt;
                                                    &lt;/div&gt;
                                                    &lt;div class=&quot;form-group&quot;&gt;
                                                        &lt;label for=&quot;CategoryName&quot; class=&quot;control-label col-md-3&quot;&gt;Category Name&lt;/label&gt;
                                                        &lt;div class=&quot;col-md-6&quot;&gt;
                                                            &lt;input type=&quot;text&quot; value.bind=&quot;datasource.currentRecord.categoryName&quot; id=&quot;CategoryName&quot; name=&quot;CategoryName&quot; class=&quot;form-control&quot; title=&quot;CategoryName&quot; placeholder=&quot;CategoryName&quot; /&gt;
                                                        &lt;/div&gt;
                                                    &lt;/div&gt;
                                                    &lt;div class=&quot;form-group&quot;&gt;
                                                        &lt;label for=&quot;Description&quot; class=&quot;control-label col-md-3&quot;&gt;Description&lt;/label&gt;
                                                        &lt;div class=&quot;col-md-6&quot;&gt;
                                                            &lt;input type=&quot;text&quot; value.bind=&quot;datasource.currentRecord.description&quot; id=&quot;Description&quot; name=&quot;Description&quot; class=&quot;form-control&quot; title=&quot;Description&quot; placeholder=&quot;Description&quot; /&gt;
                                                        &lt;/div&gt;
                                                    &lt;/div&gt;
                                                &lt;/div&gt;
                                                &lt;div class=&quot;form-group&quot;&gt;
                                                    &lt;div class=&quot;col-md-6 col-md-offset-2&quot;&gt;
                                                        &lt;!--&lt;a route-href=&quot;route:categories;&quot; class=&quot;btn btn-default&quot;&gt;&lt;span class=&quot;glyphicon glyphicon-hand-left&quot;&gt; &lt;/span&gt;  Go back&lt;/a&gt;--&gt;
                                                        &lt;input type=&quot;submit&quot; value=&quot;Save&quot; class=&quot;btn btn-primary&quot;&gt;

                                                    &lt;/div&gt;
                                                &lt;/div&gt;
                                            &lt;/div&gt;
                                        &lt;/form&gt;

                                    &lt;/div&gt;
                                &lt;/div&gt;
                                &lt;div class=&quot;tab-pane&quot; id=&quot;CategoryProductsAppTab&quot;&gt;
                                    &lt;div class=&quot;col-md-12&quot;&gt;
                                        &lt;br /&gt;
                                        &lt;div class=&quot;panel panel-success&quot;&gt;
                                            &lt;div class=&quot;panel-heading&quot;&gt;Products &lt;/div&gt;
                                            &lt;table class=&quot;table table-condensed&quot;&gt;
                                                &lt;thead&gt;
                                                    &lt;tr&gt;
                                                        &lt;th&gt;Product Name&lt;/th&gt;
                                                        &lt;th&gt;Price&lt;/th&gt;
                                                        &lt;th&gt;In Stock&lt;/th&gt;
                                                        &lt;th&gt; On Order&lt;/th&gt;

                                                    &lt;/tr&gt;
                                                &lt;/thead&gt;
                                                &lt;tbody&gt;
                                                    &lt;tr repeat.for=&quot;prod of  dataSourceProducts.displaySource&quot;&gt;
                                                        &lt;td&gt;${prod.productName}&lt;/td&gt;
                                                        &lt;!--&lt;td&gt;${prod.unitPrice} &lt;/td&gt;--&gt;
                                                        &lt;td&gt;${prod.unitPrice | currencyFormat:&#39;&#163;&#39;} &lt;/td&gt;
                                                        &lt;td&gt;${prod.unitsInStock}&lt;/td&gt;
                                                        &lt;td&gt;${prod.unitsOnOrder}&lt;/td&gt;
                                                    &lt;/tr&gt;
                                                &lt;/tbody&gt;
                                            &lt;/table&gt;
                                            &lt;p&gt;&lt;/p&gt;
                                            &lt;data-source source.bind=&quot;dataSourceProducts&quot;&gt;&lt;/data-source&gt;
                                        &lt;/div&gt;
                                    &lt;/div&gt;
                                &lt;/div&gt;
                            &lt;/div&gt;

                        &lt;/section&gt;
                    &lt;/div&gt;
                &lt;/div&gt;
            &lt;/div&gt;
        &lt;/div&gt;

    &lt;/section&gt;


    &lt;div id=&quot;confirmModal&quot; class=&quot;modal&quot;&gt;
        &lt;div class=&quot;modal-dialog&quot;&gt;
            &lt;div class=&quot;modal-content&quot;&gt;
                &lt;div class=&quot;modal-header&quot;&gt;
                    &lt;h3&gt;Confirm Delete&lt;/h3&gt;
                &lt;/div&gt;
                &lt;div class=&quot;modal-body&quot;&gt;
                    &lt;span&gt;Category To Delete&lt;/span&gt; &lt;span&gt;${datasource.currentRecord.categoryName}&lt;/span&gt;
                    &lt;p&gt;Deleting this item cannon be undone.  Are you sure?&lt;/p&gt;
                &lt;/div&gt;
                &lt;div class=&quot;modal-footer&quot;&gt;
                    &lt;button class=&quot;btn&quot; data-dismiss=&quot;modal&quot;&gt;Close&lt;/button&gt;
                    &lt;button class=&quot;btn btn-danger&quot; data-bind=&quot;click:   $root.deleteProduct&quot;&gt;Delete&lt;/button&gt;
                &lt;/div&gt;
            &lt;/div&gt;
        &lt;/div&gt;
    &lt;/div&gt;
&lt;/template&gt;

import {inject} from &quot;aurelia-framework&quot;;
import {Validation} from &#39;aurelia-validation&#39;;
import _ from &#39;lodash&#39;;
import &#39;bootstrap&#39;;
import &#39;toastr&#39;;

import {inject} from &quot;aurelia-framework&quot;;
import {SourceManager} from &quot;../modules/SourceManager&quot;;
import {CategoriesDataContext} from &quot;./categoriesDataContext&quot;;
import {CategoryCode} from &quot;./categoryInfo&quot;;

import &#39;bootstrap&#39;;
import _ from &#39;lodash&#39;;

@inject(SourceManager, CategoriesDataContext)
export class CategoryProducts {
    constructor(datasource, datacontext) {
        this.title = &quot;Category Products&quot;;
        this.datasource = new SourceManager(10);
        this.dataSourceProducts = new SourceManager(10);
        this.datacontext = datacontext;
    }
    activate() {
       return this.datacontext.getAll()
            .then(result =&gt; {
                this.dataSourceProducts.pageInit(result.products);
                this.datasource.pageInit(result.categories);
                this.datasource.selectedRecord(this.datasource.currentRecord);
                this.itemChange(this.datasource.currentRecord);
            });
    }
    
    pageChange(item) {
        var listcategoryProducts = _.filter(this.dataSourceProducts.sourceCache,
            function (pro) { return pro.categoryID === parseInt(item.item.categoryID); });
        var displayResult = {};
        this.dataSourceProducts.pageSearch(listcategoryProducts);
    }
    
    itemChange(item) {
        var listcategoryProducts = _.filter(this.dataSourceProducts.sourceCache,
            function (pro) {
                return pro.categoryID === parseInt(item.categoryID);
            });

        this.datasource.selectedRecord(item);
             this.dataSourceProducts.pageSearch(listcategoryProducts);
    }
    attached() {        
        $(&#39;#categoriesAndProductTab a:first&#39;).tab(&#39;show&#39;);
    }
   
    onKeyUp(ev) {
        if (ev.keyCode === 13) {
            this.searchAll();
        }
    }
    searchAll() {
        if (this.datasource.searchCriteria !== &#39;&#39;) {
            var filter = this.datasource.searchCriteria.trim().toLowerCase();
            var categoriesResults = _.filter(this.datasource.sourceCache,
                function (cat) {
                    return _.includes(cat.categoryName.trim().toLowerCase(),
                        filter.trim().toLowerCase()) ||
                        _.includes(cat.description.trim().toLowerCase(),
                            filter.trim().toLowerCase());

                });
            this.datasource.pageSearch(categoriesResults);
        } else {
            this.datasource.pageSearch(this.datasource.sourceCache);
        }
    }

    saveCategory() {
        this.datacontext.update(this.datasource.currentRecord);
    }

}

import {inject} from &quot;aurelia-framework&quot;;
import {ContextBaseFetch} from &quot;../modules/contextBaseFetch&quot;;
import {AppConstants} from &quot;../modules/appConstants&quot;;
@inject(ContextBaseFetch)
export class CategoriesDataContext {
      url = AppConstants.urlBase +&quot;CategoriesApi&quot;; 

    constructor(context) {
        this.context = context;
        this.context.apiName = this.url;
    }
    getAll() {
        this.context.apiName = this.url;
         return this.context.getAll()
            .then(result =&gt; { return result });
    }
    getById(id) {
        this.context.apiName = this.url;
        return this.context.getById(id)
          
    }
    deleteRecord(id) {
        this.context.apiName = this.url;
        return this.context.deleteByID(id);
            
    }
    update(category) {
        this.context.apiName = this.url;
         return this.context.update(category, category.categoryID);
    }
    save(category) {
       this.context.apiName = this.url;
       return this.context.save(category);
    }
}

import {inject} from &quot;aurelia-framework&quot;;
import {HttpClient, json} from &#39;aurelia-fetch-client&#39;;
import &#39;fetch&#39;;

@inject(HttpClient)
export class ContextBaseFetch {
    apiName;
    constructor(http) {
        http.configure(config =&gt; {
            config
                .useStandardConfiguration()
                .withBaseUrl(this.apiName);
        });

        this.http = http;
    }
    
    getAll() {
        return this.http.fetch(this.apiName)
            .then(response =&gt; response.json())
            .then(result =&gt; {return result});
    }
    getById(id) {
        return this.http.fetch(this.apiName + &quot;/&quot; + id)
            .then(response =&gt; response.json())
            .then(result =&gt; { return result });
    }
    deleteByID(id) {
        return this.http.fetch(this.apiName + &quot;/&quot; + id,
            {
                headers: { &quot;content-type&quot;: &quot;application/json; charset=utf-8&quot;},
                method: &quot;DELETE&quot;
            })
            .then(response =&gt; response.json())
            .then(result =&gt; { return result });      
    }
     deleteData(id, prodid) {
        return this.http.fetch(this.apiName + &quot;/&quot;+ id +&quot;?prodid=&quot; + prodid,
            {
                headers: { &quot;content-type&quot;: &quot;application/json; charset=utf-8&quot;},
                method: &quot;DELETE&quot;
            })
            .then(response =&gt; response.json())
            .then(result =&gt; { return result });      
    }
    update(data,id) {
       return this.http.fetch(this.apiName + &quot;/&quot; + id, {
            headers: {&quot;content-type&quot;: &quot;application/json; charset=utf-8&quot;},
            method: &quot;PUT&quot;,
            body: json(data)
            }).then(response =&gt; response.json())
            .then(result =&gt; { return result });
    }
    save(data) {
       return this.http.fetch(this.apiName + &quot;/&quot; , {
            headers: { &quot;content-type&quot;: &quot;application/json; charset=utf-8&quot; },
            method: &quot;POST&quot;,
            body: json(data)
        }).then(response =&gt; response.json())
         .then(result =&gt; { return result });
   }

}

export class CategoryInfo {
  constructor(){
      this.categoryID =&quot;0&quot;;
      this.categoryName = &quot;&quot;;
      this.description =&quot;&quot;;
      this.validation;  
    }
    static toJson(cat) {
        return {
            &quot;categoryID&quot;: cat.categoryID,
            &quot;categoryName&quot;: cat.categoryName,
            &quot;description&quot;: cat.description,
            }
    }
    static Validate(cat, validation) {
        cat.validation = validation.on(cat)
            .ensure(&quot;categoryName&quot;)
               .isNotEmpty()
               .hasLengthBetween(5, 15)
            .ensure(&quot;description&quot;)
               .isNotEmpty()
               .hasLengthBetween(10, 50);
    }
}
