    this.postalCode=&quot;&quot;;
    this.country=&quot;&quot;;
    this.phone=&quot;&quot;;
    this.fax=&quot;&quot;;
    this.validation;
    }
    static toJson(cust) {
        return {
            &quot;customerID&quot;: cust.customerID,
            &quot;companyName&quot;: cust.companyName,
            &quot;contactName&quot;: cust.contactName,
            &quot;contactTitle&quot;: cust.contactTitle,
