            &lt;/div&gt;
        &lt;/div&gt;
        &lt;div class=&quot;col-md-8&quot;&gt;
            &lt;div class=&quot;panel panel-default&quot;&gt;
             
                &lt;div class=&quot;panel-body&quot;&gt;
                    &lt;section&gt;
                        
                            &lt;ul class=&quot;nav nav-tabs&quot; id=&quot;CustomersAndOrdersTab&quot;&gt;
                                &lt;li&gt;&lt;a href=&quot;#customerAppTab&quot; data-toggle=&quot;tab&quot;&gt;customer&lt;/a&gt;&lt;/li&gt;
