                                                    &lt;tr repeat.for=&quot;order of  dataSourceOrder.displaySource&quot;&gt;
                                                        &lt;td&gt;${order.orderID}&lt;/td&gt;
                                                        &lt;td&gt;${order.orderDate | dateFormat} &lt;/td&gt;
                                                        &lt;td&gt;${order.requiredDate | dateFormat}&lt;/td&gt;
                                                        &lt;td&gt;${order.shippedDate | dateFormat}&lt;/td&gt;
                                                    &lt;/tr&gt;
                                                &lt;/tbody&gt;
                                            &lt;/table&gt;
                                            &lt;p&gt;&lt;/p&gt;
                                            &lt;data-source source.bind=&quot;dataSourceOrder&quot; &gt;&lt;/data-source&gt;
                                        &lt;/div&gt;
                                    &lt;/div&gt;
                                &lt;/div&gt;
                            &lt;/div&gt;

                    &lt;/section&gt;
            &lt;/div&gt;
        &lt;/div&gt;
         &lt;/div&gt;
    &lt;/section&gt;
&lt;/template&gt;

import {inject} from &quot;aurelia-framework&quot;;
import {Validation} from &quot;aurelia-validation&quot;;
import {SourceManager} from &quot;../modules/SourceManager&quot;;
import {CustomerDataContext} from &quot;./CustomerDataContext&quot;;
import &#39;bootstrap&#39;;
import _ from &#39;lodash&#39;;

@inject(CustomerDataContext, SourceManager,Validation)
export class CustomerOrders {
    constructor(dataContext , dataSourceOrder,validation) {
        this.title = &quot;Customer Orders&quot;;
        this.dataContext = dataContext;
        this.datasource = new SourceManager(12);
        this.dataSourceOrder = new SourceManager(10);
        this.validation = Validation;
    }

    activate() {
         return this.dataContext.getAll()
            .then(result =&gt; {
                   this.datasource.pageInit(result.customers);
                    this.dataSourceOrder.pageInit(result.orders);
                    this.itemChange(this.datasource.currentRecord);
        });
    }
    
    itemChange(item) {
        var listCustomerOrders = _.filter(this.dataSourceOrder.sourceCache,
            function (co) {
                return co.customerID === item.customerID;
            });
            this.datasource.selectedRecord(item);
           this.dataSourceOrder.pageSearch(listCustomerOrders);

    }
    attached() {
        $(&#39;#CustomersAndOrdersTab a:first&#39;).tab(&#39;show&#39;);
    }
    
    onKeyUp(ev) {
        if (ev.keyCode === 13) {
            this.searchAll();
        }
    }
    searchAll() {

        if (this.datasource.searchCriteria !== &#39;&#39;) {
            var filter = this.datasource.searchCriteria.trim().toLowerCase();
            var customerResults = _.filter(this.datasource.sourceCache,
                function (cust) {

                    return _.includes(cust.companyName.trim().toUpperCase(),
                        filter.trim().toUpperCase()) ||
                        _.includes(cust.contactName.trim().toUpperCase(),
                            filter.trim().toUpperCase()) ||
                        _.includes(cust.country.trim().toUpperCase(),
                            filter.trim().toUpperCase());
                });
            this.datasource.pageSearch(customerResults);
            this.itemChange(this.datasource.currentRecord);
        } else {
            this.datasource.pageSearch(this.datasource.sourceCache);
            this.itemChange(this.datasource.currentRecord);
        }

    }
    saveCustomer() {
        this.dataContext.update(this.datasource.currentRecord);
    }
}

import {Validation} from &#39;aurelia-validation&#39;;

export class CustomerInfo {
    constructor(){
    this.customerID=&quot;&quot;;
    this.companyName=&quot;&quot;;
    this.contactName =&quot;&quot;;
    this.contactTitle=&quot;&quot;;
    this.address=&quot;&quot;;
    this.city=&quot;&quot;;
    this.region=&quot;&quot;;
    this.postalCode=&quot;&quot;;
    this.country=&quot;&quot;;
    this.phone=&quot;&quot;;
    this.fax=&quot;&quot;;
    this.validation;
    }
    static toJson(cust) {
        return {
            &quot;customerID&quot;: cust.customerID,
            &quot;companyName&quot;: cust.companyName,
            &quot;contactName&quot;: cust.contactName,
            &quot;contactTitle&quot;: cust.contactTitle,
            &quot;address&quot;: cust.address,
            &quot;city&quot;: cust.city,
            &quot;region&quot;: cust.region,
            &quot;postalCode&quot;: cust.postalCode,
            &quot;country&quot;: cust.country,
            &quot;phone&quot;: cust.phone,
            &quot;fax&quot;: cust.fax
        };
    }
    static Validate(cust, validation) {
        cust.validation = validation.on(cust)
            .ensure(&quot;customerID&quot;)
               .isNotEmpty()
               .hasLengthBetween(5, 5)
            .ensure(&quot;companyName&quot;)
               .isNotEmpty()
               .hasLengthBetween(3, 40)
            .ensure(&quot;contactName&quot;)
               .isNotEmpty()
               .hasLengthBetween(3, 30)
            .ensure(&quot;contactTitle&quot;)
               .isNotEmpty()
               .hasLengthBetween(3, 30)
            .ensure(&quot;address&quot;)
               .isNotEmpty()
               .hasMaxLength(60)
            .ensure(&quot;city&quot;)
               .isNotEmpty()
               .hasMaxLength(15)
            .ensure(&quot;postalCode&quot;)
               .isNotEmpty()
               .hasMaxLength(10)
            .ensure(&quot;country&quot;)
               .isNotEmpty()
               .hasMaxLength(15)
            .ensure(&quot;phone&quot;)
              .isNotEmpty()
              .hasMaxLength(24);
    }

}

import {inject} from &quot;aurelia-framework&quot;;
import {ContextBaseFetch} from &quot;../modules/contextBaseFetch&quot;;
import {AppConstants} from &quot;../modules/appConstants&quot;;
@inject(ContextBaseFetch)
export class CustomerDataContext {
    apiUrl = AppConstants.urlBase +&quot;CustomersApi&quot;; 

    constructor(context) {
        this.context = context;
        this.context.apiName = this.apiUrl;
    }
    getAll() {
        this.context.apiName = this.apiUrl;
         return this.context.getAll()
            .then(result =&gt; { return result });
    }
    getById(id) {
        this.context.apiName = this.apiUrl;
        return this.context.getById(id)
          
    }
    deleteRecord(id) {
        this.context.apiName = this.apiUrl;
        return this.context.deleteByID(id);
            
    }
    update(customer) {
        this.context.apiName = this.apiUrl;
         return this.context.update(customer, customer.customerID);
    }
    save(customer) {
       this.context.apiName = this.apiUrl;
       return this.context.save(customer);
    }
}

import {inject} from &quot;aurelia-framework&quot;;
import {HttpClient, json} from &#39;aurelia-fetch-client&#39;;
import &#39;fetch&#39;;

@inject(HttpClient)
export class ContextBaseFetch {
    apiName;
    constructor(http) {
        http.configure(config =&gt; {
            config
                .useStandardConfiguration()
                .withBaseUrl(this.apiName);
        });

        this.http = http;
    }
    
    getAll() {
        return this.http.fetch(this.apiName)
            .then(response =&gt; response.json())
            .then(result =&gt; {return result});
    }
    getById(id) {
        return this.http.fetch(this.apiName + &quot;/&quot; + id)
            .then(response =&gt; response.json())
            .then(result =&gt; { return result });
    }
    deleteByID(id) {
        return this.http.fetch(this.apiName + &quot;/&quot; + id,
            {
                headers: { &quot;content-type&quot;: &quot;application/json; charset=utf-8&quot;},
                method: &quot;DELETE&quot;
            })
            .then(response =&gt; response.json())
            .then(result =&gt; { return result });      
    }
     deleteData(id, prodid) {
        return this.http.fetch(this.apiName + &quot;/&quot;+ id +&quot;?prodid=&quot; + prodid,
            {
                headers: { &quot;content-type&quot;: &quot;application/json; charset=utf-8&quot;},
                method: &quot;DELETE&quot;
            })
            .then(response =&gt; response.json())
            .then(result =&gt; { return result });      
    }
    update(data,id) {
       return this.http.fetch(this.apiName + &quot;/&quot; + id, {
            headers: {&quot;content-type&quot;: &quot;application/json; charset=utf-8&quot;},
            method: &quot;PUT&quot;,
            body: json(data)
            }).then(response =&gt; response.json())
            .then(result =&gt; { return result });
    }
    save(data) {
       return this.http.fetch(this.apiName + &quot;/&quot; , {
            headers: { &quot;content-type&quot;: &quot;application/json; charset=utf-8&quot; },
            method: &quot;POST&quot;,
            body: json(data)
        }).then(response =&gt; response.json())
         .then(result =&gt; { return result });
   }

}
