import {inject} from &quot;aurelia-framework&quot;;
import {SourceManager} from &quot;../modules/SourceManager&quot;;
import {OrdersDataContext} from &quot;./OrdersDataContext&quot;;
import {AppConstants} from &quot;../modules/appConstants&quot;;

import _ from &#39;lodash&#39;;

@inject(OrdersDataContext, SourceManager)
export class Orders {
   
    constructor(datacontext, oderDetailsDataSource) {
        this.datacontext = datacontext 
        this.datasource = new SourceManager(12);
        this.oderDetailsDataSource = new SourceManager(10);
        this.subtotal = 0;
        this.total = 0;
        this.VAT = 0;
    }

    activate() {
        return this.datacontext.getAllOrders()
            .then(result =&gt; {
                this.datasource.pageInit(result.orders);
                this.oderDetailsDataSource.pageInit(result.orderDetails);
                this.itemChange(this.datasource.currentRecord);
            });
    }

    itemChange(item) {
        var listOrderDetails = _.filter(this.oderDetailsDataSource.sourceCache,
            function (co) {
                return co.orderID === item.orderID;
            });
        var _subtotal = 0;
        this.datasource.selectedRecord(item);
        this.oderDetailsDataSource.pageSearch(listOrderDetails);
        _.each(listOrderDetails, function (od) {
            _subtotal += od.extendedPrice;
        });
        this.subtotal = _subtotal; 
        this.VAT = (_subtotal * AppConstants.VAT)
        this.total = (this.subtotal + this.VAT)
    }
  

    onKeyUp(ev) {
        if (ev.keyCode === 13) {
            this.searchAll();
        }
    }
    searchAll() {

        if (this.datasource.searchCriteria !== &#39;&#39;) {
            var filter = this.datasource.searchCriteria.trim().toLowerCase();
            var customerResults = _.filter(this.datasource.sourceCache,
                function (ord) {

                    return _.includes(ord.orderID.toString().trim(),
                        filter.trim()) ||
                        _.includes(ord.customerID.trim().toUpperCase(),
                            filter.trim().toUpperCase()) ||
                       _.includes(ord.companyName.trim().toUpperCase(),
                            filter.trim().toUpperCase()) ||
                       _.includes(ord.country.trim().toUpperCase(),
                            filter.trim().toUpperCase());
                });
            this.datasource.pageSearch(customerResults);
            this.itemChange(this.datasource.currentRecord);
        } else {
            this.datasource.pageSearch(this.datasource.sourceCache);
            this.itemChange(this.datasource.currentRecord);
        }

    }

}

import {inject} from &quot;aurelia-framework&quot;;
import {Validation} from &#39;aurelia-validation&#39;;
@inject(Validation)
export class OrderInfo {
      
      constructor(validation) {
         this.companyName = &#39;&#39;;
         this.contactName= &#39;&#39;;
         this.address= &#39;&#39;;
         this.city= &#39;&#39;;
         this.country= &#39;&#39;;
         this.region= &#39;&#39;;
         this.postalCode= &#39;&#39;;
         this.orderID= 0;
         this.customerID= &#39;&#39;;
         this.employeeID=0
         this.orderDate = new Date();
         this.requiredDate = new Date();
         this.shippedDate = new Date();
         this.shipVia = 1;
         this.freight = 1
         this.shipName =&quot;&quot;;
         this.shipAddress =&quot;&quot;;
         this.shipCity =&quot;&quot;;
         this.shipRegion =&quot;&quot;;
         this.shipPostalCode =&quot;&quot;;
         this.shipCountry =&quot;&quot;;
         this.validation = validation;
   
      }

    static toJson(ord) {
        return {
            &quot;companyName&quot;: ord.companyName,
            &quot;contactName&quot;: ord.contactName,
            &quot;address&quot;: ord.address,
            &quot;city&quot;: ord.city,
            &quot;country&quot;: ord.country,
            &quot;region&quot;: ord.region,
            &quot;postalCode&quot;: ord.postalCode,
            &quot;orderID&quot;: ord.orderID,
            &quot;customerID&quot;: ord.customerID,
            &quot;employeeID&quot;: ord.employeeID,
            &quot;orderDate&quot;: ord.orderDate,
            &quot;requiredDate&quot;: ord.requiredDate,
            &quot;shippedDate&quot;: ord.shippedDate,
            &quot;shipVia&quot;: ord.shipVia,
            &quot;freight&quot;: ord.freight,
            &quot;shipName&quot;: ord.shipName,
            &quot;shipAddress&quot;: ord.shipAddress,
            &quot;shipCity&quot;: ord.shipCity,
            &quot;shipRegion&quot;: ord.shipRegion,
            &quot;shipPostalCode&quot;: ord.shipPostalCode,
            &quot;shipCountry&quot;: ord.shipCountry
        }
    }
    static Validate(ord, validation) {
        ord.validation = validation.on(ord)
            .ensure(&quot;companyName&quot;)
                .isNotEmpty()
                .hasLengthBetween(3, 40)
            .ensure(&quot;address&quot;)
                .isNotEmpty()
                .hasMaxLength(60)
            .ensure(&quot;city&quot;)
                .isNotEmpty()
                .hasMaxLength(15)
            .ensure(&quot;postalCode&quot;)
                .isNotEmpty()
                .hasMaxLength(15)
            .ensure(&quot;country&quot;)
                .isNotEmpty()
                .hasMaxLength(15)
            .ensure(&quot;orderDate&quot;)
                .isNotEmpty()
            .ensure(&quot;requiredDate&quot;)
                .isNotEmpty()
            .ensure(&quot;shippedDate&quot;)
                .isNotEmpty()
            .ensure(&quot;shipName&quot;)
                 .isNotEmpty()
                .hasMaxLength(40)
            .ensure(&quot;shipAddress&quot;)
                .isNotEmpty()
                .hasMaxLength(60)
            .ensure(&quot;shipCity&quot;)
                .isNotEmpty()
                .hasMaxLength(15)
            .ensure(&quot;shipPostalCode&quot;)
                .isNotEmpty()
                .hasMaxLength(15)
            .ensure(&quot;shipCountry&quot;)
                .isNotEmpty()
                .hasMaxLength(15)
    }
}

