&lt;template&gt;
    &lt;!--&lt;require from=&#39;../modules/data-source.html&#39;&gt;&lt;/require&gt;--&gt;
    &lt;section&gt;
        &lt;div class=&quot;col-md-4&quot;&gt;
            &lt;div class=&quot;panel panel-default&quot;&gt;
                &lt;div class=&quot;panel-heading&quot;&gt;
                    &lt;div class=&quot;panel-title&quot;&gt;
                        &lt;h5&gt; shippers&lt;/h5&gt;
                    &lt;/div&gt;
                &lt;/div&gt;
                &lt;div class=&quot;panel-body&quot;&gt;
                    &lt;div class=&quot;input-group&quot;&gt;
                        &lt;input type=&quot;text&quot; value.bind=&quot;datasource.searchCriteria&quot; keyup.delegate=&quot;onKeyUp($event)&quot;
                               class=&quot;form-control&quot; placeholder=&quot;Shipper Name&quot;&gt;
                        &lt;div class=&quot;input-group-btn&quot;&gt;
                            &lt;button type=&quot;button&quot; class=&quot;btn btn-success&quot; tabindex=&quot;-1&quot;
                                    click.trigger=&quot;searchAll()&quot;&gt;
                                Search
                            &lt;/button&gt;
                            &lt;button type=&quot;button&quot; class=&quot;btn btn-success dropdown-toggle&quot; data-toggle=&quot;dropdown&quot; tabindex=&quot;-1&quot;&gt;
                                &lt;span class=&quot;caret&quot;&gt;&lt;/span&gt;
                                &lt;span class=&quot;sr-only&quot;&gt;Toggle Dropdown&lt;/span&gt;
                            &lt;/button&gt;
                            &lt;ul class=&quot;dropdown-menu pull-right&quot;&gt;&lt;/ul&gt;
                        &lt;/div&gt;&lt;!-- /btn-group --&gt;

                    &lt;/div&gt;&lt;!-- /input-group --&gt;
                    &lt;br /&gt;
                    &lt;table class=&quot;table table-condensed table-responsive&quot;&gt;
                        &lt;thead&gt;
                            &lt;tr&gt;
                                &lt;td&gt;Shipper Name&lt;/td&gt;
                            &lt;/tr&gt;
                        &lt;/thead&gt;
                        &lt;tbody&gt;
                            &lt;tr repeat.for=&quot;shipper of  datasource.displaySource&quot;&gt;
                                &lt;td&gt;&lt;a href=&quot;#&quot; click.delegate=&quot;$parent.itemChange(shipper)&quot;&gt;${shipper.companyName}&lt;/a&gt;&lt;/td&gt;
                            &lt;/tr&gt;
                        &lt;/tbody&gt;
                    &lt;/table&gt;
                      &lt;data-source source.bind=&quot;datasource&quot; click.delegate=&quot;itemChange(datasource.currentRecord)&quot;&gt;&lt;/data-source&gt;
                &lt;/div&gt;
            &lt;/div&gt;
        &lt;/div&gt;
        &lt;div class=&quot;col-md-8&quot;&gt;
            &lt;div class=&quot;panel panel-default&quot;&gt;
                &lt;div class=&quot;panel-heading&quot;&gt;
                    &lt;div class=&quot;panel-title&quot;&gt;
                        &lt;h5&gt;Shipper Details&lt;/h5&gt;
                    &lt;/div&gt;
                &lt;/div&gt;
                &lt;div class=&quot;panel-body&quot;&gt;
                    &lt;form class=&quot;form-horizontal&quot; validate.bind=&quot;currentShippers.validation&quot; id=&quot;formShipper&quot; role=&quot;form&quot;&gt;
                        &lt;div&gt;
                            &lt;div data-bind=&quot;with: ShipperModel.currentRecord&quot;&gt;
                                &lt;div class=&quot;form-group&quot;&gt;
                                    &lt;label for=&quot;ShipperID&quot; class=&quot;control-label col-md-3&quot;&gt;Shipper ID&lt;/label&gt;
                                    &lt;div class=&quot;col-md-6&quot;&gt;
                                        &lt;input type=&quot;text&quot; value.bind=&quot;currentShippers.shipperID&quot; readonly id=&quot;ShipperID&quot; name=&quot;ShipperID&quot; class=&quot;form-control&quot; title=&quot;ShipperID&quot; placeholder=&quot;ShipperID&quot; /&gt;
                                    &lt;/div&gt;
                                &lt;/div&gt;
                                &lt;div class=&quot;form-group&quot;&gt;
                                    &lt;label for=&quot;ShipperName&quot; class=&quot;control-label col-md-3&quot;&gt;Company Name&lt;/label&gt;
                                    &lt;div class=&quot;col-md-6&quot;&gt;
                                        &lt;input type=&quot;text&quot; value.bind=&quot;currentShippers.companyName&quot; validate=&quot;companyName&quot; id=&quot;ShipperName&quot; name=&quot;ShipperName&quot; class=&quot;form-control&quot; title=&quot;ShipperName&quot; placeholder=&quot;ShipperName&quot; /&gt;
                                    &lt;/div&gt;
                                &lt;/div&gt;
                                &lt;div class=&quot;form-group&quot;&gt;
                                    &lt;label for=&quot;phone&quot; class=&quot;control-label col-md-3&quot;&gt;Phone&lt;/label&gt;
                                    &lt;div class=&quot;col-md-6&quot;&gt;
                                        &lt;input type=&quot;text&quot; value.bind=&quot;currentShippers.phone&quot; id=&quot;phoneid&quot; validate=&quot;phone&quot; name=&quot;phone&quot; class=&quot;form-control&quot; title=&quot;QuantityPerUnit&quot; placeholder=&quot;QuantityPerUnit&quot; /&gt;
                                    &lt;/div&gt;
                                &lt;/div&gt;
                            &lt;/div&gt;
                            &lt;br /&gt;
                            &lt;br /&gt;
                            &lt;div class=&quot;form-group&quot;&gt;

                                &lt;div class=&quot;col-md-6 col-md-offset-3&quot;&gt;
                                    &lt;a class=&quot;btn btn-primary&quot; click.trigger=&quot;showAddNewModal()&quot;&gt;&lt;span class=&quot;fa fa-plus&quot;&gt; &lt;/span&gt; Add New&lt;/a&gt;
                                    &lt;a class=&quot;btn btn-primary&quot; style=&quot;margin-left:15px&quot; disabled.bind=&quot;!currentShippers.validation.result.isValid&quot; click.trigger=&quot;saveShipper()&quot;&gt;&lt;span class=&quot;glyphicon glyphicon-floppy-save&quot;&gt; &lt;/span&gt; Save&lt;/a&gt;
                                    &lt;a class=&quot;btn btn-danger&quot; style=&quot;margin-left:15px&quot; click.trigger=&quot;showDeleteModal()&quot;&gt;&lt;span class=&quot;fa fa-trash-o&quot;&gt; &lt;/span&gt; Delete&lt;/a&gt;
                                &lt;/div&gt;
                            &lt;/div&gt;

                        &lt;/div&gt;
                    &lt;/form&gt;
                &lt;/div&gt;
            &lt;/div&gt;
        &lt;/div&gt;
    &lt;/section&gt;
    &lt;div id=&quot;newShipplingModal&quot; class=&quot;modal fade&quot;&gt;
        &lt;div class=&quot;modal-dialog modal-lg&quot;&gt;
            &lt;div class=&quot;modal-content&quot;&gt;
                &lt;div class=&quot;modal-header&quot;&gt;
                    &lt;h3&gt;Confirm New Shipper&lt;/h3&gt;
                &lt;/div&gt;
                &lt;div class=&quot;modal-body&quot;&gt;
                    &lt;form class=&quot;form-horizontal&quot; validate.bind=&quot;newShipper.validation&quot; id=&quot;formNewShipper&quot; role=&quot;form&quot; submit.trigger=&quot;saveNewShipper()&quot;&gt;
                        &lt;div&gt;
                            &lt;div&gt;
                                &lt;div class=&quot;form-group&quot;&gt;
                                    &lt;label for=&quot;companyName&quot; class=&quot;control-label col-md-3&quot;&gt;company Name&lt;/label&gt;
                                    &lt;div class=&quot;col-md-9&quot;&gt;
                                        &lt;input type=&quot;text&quot; value.bind=&quot;newShipper.companyName&quot; validate=&quot;companyName&quot; id=&quot;companyName&quot; required name=&quot;companyName&quot; class=&quot;form-control&quot; data-minlength=&quot;5&quot; maxlength=&quot;25&quot; title=&quot;company Name&quot; placeholder=&quot;company Name&quot; /&gt;
                                    &lt;/div&gt;
                                &lt;/div&gt;
                                &lt;div class=&quot;form-group&quot;&gt;
                                    &lt;label for=&quot;phone&quot; class=&quot;control-label col-md-3&quot;&gt;phone&lt;/label&gt;
                                    &lt;div class=&quot;col-md-9&quot;&gt;
                                        &lt;input type=&quot;text&quot; value.bind=&quot;newShipper.phone&quot; validate=&quot;phone&quot; id=&quot;phone&quot; name=&quot;phone&quot; class=&quot;form-control&quot; title=&quot;phone&quot; data-minlength=&quot;10&quot; placeholder=&quot;phone&quot; /&gt;
                                    &lt;/div&gt;
                                &lt;/div&gt;
                                &lt;hr /&gt;
                                &lt;div class=&quot;form-group&quot;&gt;
                                    &lt;div class=&quot;col-md-6 col-md-offset-3&quot;&gt;
                                        &lt;input type=&quot;submit&quot; disabled.bind=&quot;!newShipper.validation.result.isValid&quot; value=&quot;Save New Shipper&quot; class=&quot;btn btn-primary btn-lg&quot;&gt;
                                    &lt;/div&gt;
                                &lt;/div&gt;
                            &lt;/div&gt;

                        &lt;/div&gt;
                    &lt;/form&gt;

                &lt;/div&gt;
                &lt;div class=&quot;modal-footer&quot;&gt;
                    &lt;button class=&quot;btn&quot; data-dismiss=&quot;modal&quot;&gt;Close&lt;/button&gt;
                &lt;/div&gt;
            &lt;/div&gt;
        &lt;/div&gt;
    &lt;/div&gt;
    &lt;!--confirm Delete Product Modal starts here --&gt;
    &lt;div id=&quot;confirmDeleteShipperModal&quot; class=&quot;modal&quot;&gt;
        &lt;div class=&quot;modal-dialog&quot;&gt;
            &lt;div class=&quot;modal-content&quot;&gt;
                &lt;div class=&quot;modal-header&quot;&gt;
                    &lt;h3&gt;Confirm Delete&lt;/h3&gt;
                &lt;/div&gt;
                &lt;div class=&quot;modal-body&quot;&gt;
                    &lt;span&gt;Shipper To Delete&lt;/span&gt; &lt;span&gt;${currentShippers.companyName}&lt;/span&gt;
                    &lt;p&gt;Deleting this item cannon be undone.  Are you sure?&lt;/p&gt;
                &lt;/div&gt;
                &lt;div class=&quot;modal-footer&quot;&gt;
                    &lt;button class=&quot;btn&quot; data-dismiss=&quot;modal&quot;&gt;Close&lt;/button&gt;
                    &lt;button class=&quot;btn btn-danger&quot; click.delegate=&quot;deleteProduct()&quot;&gt;Delete&lt;/button&gt;
                &lt;/div&gt;
            &lt;/div&gt;
        &lt;/div&gt;
    &lt;/div&gt;
    &lt;!--confirm Delete Product Modal ends here --&gt;
&lt;/template&gt;

import {inject} from &quot;aurelia-framework&quot;;
import {SourceManager} from &quot;../modules/SourceManager&quot;;
import {ShippersDataContext} from &quot;./shippersDataContext&quot;;
import {ShipperInfo} from &quot;./shippersInfo&quot;;
import {Validation} from &#39;aurelia-validation&#39;;

import &#39;bootstrap&#39;;
import _ from &#39;lodash&#39;;

@inject(ShippersDataContext, SourceManager, Validation)
export class shippers {
    title;
    showing;
    currentShippers;
    newShipper;

    constructor(dataContext, datasource, validation ) {
        this.datasource = new SourceManager(12);
        this.dataContext = dataContext;
        this.validation = validation;
    }
    activate() {
        return this.dataContext.getAll()
            .then(result =&gt; {
                this.datasource.pageInit(result.shippers);
                this.itemChange(this.datasource.currentRecord);
            });
    }
    onKeyUp(ev) {
        if (ev.keyCode === 13) {
            this.searchAll();
        }
    }

    searchAll() {

        if (this.datasource.searchCriteria !== &quot;&quot;) {
            var filter = this.datasource.searchCriteria.trim().toLowerCase();
            var ShippersResults = _.filter(this.datasource.sourceCache,
                function (shi) {
                    return _.includes(shi.companyName.trim().toLowerCase(),
                        filter.trim().toLowerCase());
                });
            this.datasource.pageSearch(ShippersResults);
        } else {
            this.datasource.pageInit(this.datasource.sourceCache);
        }
    }
    itemChange(item) {
        this.datasource.selectedRecord(item);
        this.currentShippers = item;
        ShipperInfo.Validate(this.currentShippers, this.validation);

    }
    saveShipper() {
        var updateShuppers = ShipperInfo.getJson(this.currentShippers);
        this.currentShippers.validation.validate().then(() =&gt; {
             this.dataContext.update(updateShuppers)
                 .then(Shipper =&gt; {
                     Shipper = Shipper;
                     toastr.success(Shipper.companyName + &quot; Saved&quot;, &quot;shippers&quot;);
                   })
        });


    }

    /* modals */

    showDeleteModal() {
        $(&quot;#confirmDeleteShipperModal&quot;).modal(&#39;show&#39;);
    }
    deleteProduct() {

        var product = this.datasource.currentRecord;
        return this.dataContext.deleteShippers(this.currentShippers.shipperID)
            .then(deleteShipper =&gt; {
                deleteShipper = deleteShipper;
                toastr.warning(this.currentShippers.companyName + &quot; Deleted&quot;, &quot;Shippers&quot;);
                this.refreshDelete(this.currentShippers.shipperID);
            });
    }
    refreshDelete(id) {
        var deleteIndex = _.findIndex(this.datasource.sourceCache,
            function (ship) {
                return ship.shipperID == id;
            });
        this.datasource.sourceCache.splice(deleteIndex, 1);
        this.datasource.pageInit(this.datasource.sourceCache);
        this.datasource.firstPage();
        this.itemChange(this.datasource.currentRecord);
        $(&quot;#confirmDeleteShipperModal&quot;).modal(&#39;hide&#39;);
    }
    closeModal() {
        this.showing = false;
    }

    showAddNewModal() {
       this.newShipper = new ShipperInfo() 
       this.newShipper.companyName =&quot;&quot;,
       this.newShipper.phone= &quot;&quot;
       $(&quot;#newShipplingModal&quot;).modal(&#39;show&#39;);
       ShipperInfo.Validate(this.newShipper, this.validation);

     }

    closeAddnewModal() {
        this.showing = false;
    }
    saveNewShipper() {
        var Shipper: any;
        this.newShipper.validation.validate().then(() =&gt; {
            var _newShippers = ShipperInfo.getJson(this.newShipper);

            this.dataContext.save(_newShippers)
                .then(Shipper =&gt; {
                Shipper = Shipper; this.refresh(Shipper);
                toastr.success(this.newShipper.companyName + &quot; Saved&quot;, &quot;shippers&quot;);})
        });
    }
    refresh(Shipper) {
        this.datasource.dataSource.push(Shipper);
        this.datasource.firstPage();
        $(&quot;#newShipplingModal&quot;).modal(&#39;hide&#39;);
    }
}

export class ShipperInfo {
    shipperID;
    companyName;
    phone;
    validation;
    static getJson(shipper) {
        return {
            &quot;shipperID&quot;: shipper.shipperID,
            &quot;companyName&quot;: shipper.companyName,
            &quot;phone&quot;: shipper.phone
        }
    }
    static Validate(shipper, validation) {
        shipper.validation = validation.on(shipper)
            .ensure(&quot;companyName&quot;)
                .isNotEmpty()
                .hasMaxLength(40)
            .ensure(&quot;phone&quot;)
                .isNotEmpty()
                .hasMaxLength(24);
    }
}

import {inject} from "aurelia-framework";
import {ContextBaseFetch} from "../modules/contextBaseFetch";
import {AppConstants} from "../modules/appConstants";

@inject(ContextBaseFetch)
export class ShippersDataContext {
    apiUrl = AppConstants.urlBase +"ShippersApi";
    constructor(context) {
        this.context = context;
        this.context.apiName = this.apiUrl;
    }
    getAll() {
        this.context.apiName = this.apiUrl;
        return this.context.getAll();
    }
    getById(id) {
        this.context.apiName = this.apiUrl;
        return this.context.getById(id);
    }
    deleteShippers(id) {
        this.context.apiName = this.apiUrl;
        return this.context.deleteByID(id);
    }
    update(shippers) {
        this.context.apiName = this.apiUrl;
        return this.context.update(shippers);
    }
    save(shippers) {
        this.context.apiName = this.apiUrl;
        return this.context.save(shippers);
    }
}

import {inject} from &quot;aurelia-framework&quot;;
export class SourceManager {
    constructor(page) {
        this.itemPerPage = page;
        this.dataSource;
        this.displaySource;
        this.currentRecord;
        this.sourceCache;
        this.currentPage;
        this.searchCriteria;
        this.totalPage;
    }
    selectedRecord(item) {
        this.currentRecord = item;
    }
    pageCount() {
        this.totalPage = Math.ceil(this.dataSource.length / this.itemPerPage) || 1;
        return this.totalPage;
    }
    nextPage() {
        if (this.currentPage &lt; (this.pageCount() - 1)) {
            this.currentPage = this.currentPage + 1;
            this.showpage();
        }
    }
    previeusPage() {
        if (this.currentPage &gt; 0) {
            this.currentPage = this.currentPage - 1;
            this.showpage();
        }
    }
    firstPage() {
        this.currentPage = 0;
        this.showpage();
    };
    lastPage() {
        this.currentPage = this.pageCount() - 1;
        this.showpage();
    };
    goToPage(page) {
        this.currentPage = page;
        this.showpage();
        this.pageCount();
    };
    pageSearch(items) {
        this.currentPage = 0;
        this.dataSource = items;
        this.showpage();
        this.pageCount();
    };
    pageInit(items) {
        this.currentPage = 0;
        this.dataSource = items;
        this.sourceCache = items;
        this.showpage();
        this.pageCount();
    };
    deleteItem(deleteIndex) {
        this.sourceCache.splice(deleteIndex, 1);
        this.pageInit(this.sourceCache);
        this.firstPage();
    }
    showpage() {
        var size = this.itemPerPage;
        var start = this.currentPage * size;
        this.displaySource = this.dataSource.slice(start, start + size);
        if (this.displaySource.length) {
            this.currentRecord = this.displaySource[0];
            this.selectedRecord(this.currentRecord);
            this.pageCount();
        }

        return this.displaySource;
    };
}
